  <div class="slick-image-slide">  	<?php 	$sliderurl = get_post_meta( get_the_ID(),'wpsisac_slide_link', true );	$slider_img 	= wpsisac_get_post_featured_image( $post->ID, $sliderimage_size, true );	echo ($sliderurl !='' ? '<a href="'.$sliderurl.'">' : '');	if($image_size == '' || $image_size == 'medium')  {	?>			<div class="slick-image-slide-wrap"><img src="<?php echo $slider_img; ?>" alt="<?php the_title(); ?>" />	</div>		 		<?php } elseif ($image_size == 'large') {	?>				<div class="slick-image-slide-wrap" <?php echo $slider_height_css ; ?>><img src="<?php echo $slider_img; ?>" alt="<?php the_title(); ?>" />	</div>				<?php } elseif ( $image_size == 'full') {				?>				<div class="slick-image-slide-wrap" <?php echo $slider_height_css ; ?>><img src="<?php echo $slider_img; ?>" alt="<?php the_title(); ?>" />	</div>		<?php } elseif ( $image_size == 'thumbnail') {	?>			<div class="slick-image-slide-wrap"><img src="<?php echo $slider_img; ?>" alt="<?php the_title(); ?>" />	</div>						<?php }  else { ?>			<div class="slick-image-slide-wrap"><img src="<?php echo $slider_img; ?>" alt="<?php the_title(); ?>" />	</div>					<?php	}	echo ($sliderurl !='' ? '</a>' : ''); ?></div>